    private void btnViewGradesActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnViewGradesActionPerformed
        // when clicked should go to VIEW GRADES window
        Viewgrades viewgrd = new Viewgrades();
        viewgrd.setVisible(true);
        viewgrd.pack();
        viewgrd.setLocationRelativeTo(null);
        viewgrd.setDefaultCloseOperation(viewgrd.EXIT_ON_CLOSE);
        this.dispose();
    }//GEN-LAST:event_btnViewGradesActionPerformed

